import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Image, TextInput, Button, TouchableOpacity} from 'react-native';


export default function App() {
  const nomes = ["Fulano", "Beltrano", "Ciclano", "Tobias", "Jurelson", "Josefina"];

  const img = require('./assets/chur.png')

  return (
    <View style={styles.container}>

      <View style={styles.primeira}>
        <Text>Bem vindo, {nomes[0]}</Text>
        <StatusBar style="auto"/>
        <TextInput style={styles.input} placeholder="Fetey"/>
        <TouchableOpacity></TouchableOpacity>
        {
          nomes.map(nome => {
            return(
              <Text>{nome}</Text>
            )
          })
        }
      </View>

      <View style={styles.segunda}>
        <Image source={img} style={styles.imgp}/>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  imgp: {
    height: 120,
    width: 100,
  },
  input: {
    height:20,
    width:80,
    borderBottomWidth: '1px',
    borderBottomColor: '#000',
  }
});
